import { Component, EventEmitter, OnInit, Output } from "@angular/core";

@Component({
  selector: "app-buttons",
  templateUrl: "./buttons.component.html",
  styleUrls: ["./buttons.component.scss"],
})
export class ButtonsComponent implements OnInit {
  @Output() clickEvent = new EventEmitter();

  constructor() {}

  executeButton(public command: string) {
    this.clickEvent.emit(command);
  }

  ngOnInit() {}
}
<p>
  <button class="start-btn" (click)="executeButton('start')">Start</button>
  <button class="start-btn" (click)="executeButton('stop')">Stop</button>
  <button class="start-btn" (click)="executeButton('reset')">Reset</button>
</p>
